// SPDX-License-Identifier: MIT
pragma solidity ~0.8.17;

/**
	*@dev All contracts that need to implement upgrades should inherit this `DAOStore` contract, 
	* and it should be the first parent class inherited
	*/
contract DAOStore {
	struct EnumerableSet_Set {
					bytes32[] _values;
					mapping(bytes32 => uint256) _indexes;
}
struct EnumerableSet_Bytes32Set {
				EnumerableSet_Set _inner;
}
struct EnumerableMap_Bytes32ToBytes32Map {
			EnumerableSet_Bytes32Set _keys;
			mapping(bytes32 => bytes32) _values;
}
struct EnumerableMap_UintToAddressMap {
		EnumerableMap_Bytes32ToBytes32Map _inner;
}

	address _impl;
	uint8 _initialized;
	bool _initializing;
	mapping(bytes4 => bool) _supportedInterfaces;
	address _host;
	address _operator;
	string _description;
	uint256[8] __;
	address _root;
	string _name;
	string _mission;
	EnumerableMap_UintToAddressMap _modules;
	string image;
	bytes extend;
	address _daos;
	address _unlockOperator;
	uint256[46] ___1; // impl address
}

/**
 * @dev The context here is that the externally exposed contract entities are only used to store data, 
 * and the implemented business logic should be placed in the impl
 * Any method that calls this contract will be directed to `fallback()`, 
 * and then use `delegatecall()` to call the actual implementation and pass the current data context to impl
 */
contract DAOProxy is DAOStore {
	// The size allocated by Layout Store should be specified by dynamic compilation, 
	// and the storage size needs to be read from the original contract

	constructor(address impl_) public {
		_impl = impl_;
	}

	fallback() external payable {
		(bool suc, bytes memory _data) = _impl.delegatecall(msg.data);
		assembly {
			let len := mload(_data)
			let data := add(_data, 0x20)
			switch suc
			case 0 { revert(data, len) }
			default { return(data, len) }
		}
	}
}
