// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

/**
	*@dev All contracts that need to implement upgrades should inherit this `VotePoolStore` contract, 
	* and it should be the first parent class inherited
	*/
contract VotePoolStore {
	struct IVotePool_Proposal {
		uint256 id;
		string name;
		string description;
		address origin;
		uint256 originId;
		address[] target;
		uint256 lifespan;
		uint256 expiry;
		uint256 passRate;
		int256 loopCount;
		uint256 loopTime;
		uint256 voteTotal;
		uint256 agreeTotal;
		uint256 executeTime;
		uint256 idx;
		bool isAgree;
		bool isClose;
		bool isExecuted;
		bytes[] data;
}

	address _impl;
	uint8 _initialized;
	bool _initializing;
	mapping(bytes4 => bool) _supportedInterfaces;
	address _host;
	string _description;
	mapping(uint256 => IVotePool_Proposal) _proposalMap;
	uint256[] _proposalList;
	mapping(uint256 => mapping(uint256 => int256)) _votes;
	uint256 lifespan;
	uint256[16] __; // impl address
}

/**
 * @dev The context here is that the externally exposed contract entities are only used to store data, 
 * and the implemented business logic should be placed in the impl
 * Any method that calls this contract will be directed to `fallback()`, 
 * and then use `delegatecall()` to call the actual implementation and pass the current data context to impl
 */
contract VotePoolProxy is VotePoolStore {
	// The size allocated by Layout Store should be specified by dynamic compilation, 
	// and the storage size needs to be read from the original contract

	constructor(address impl) public {
		_impl = impl;
	}

	fallback() external payable {
		(bool suc, bytes memory _data) = _impl.delegatecall(msg.data);
		assembly {
			let len := mload(_data)
			let data := add(_data, 0x20)
			switch suc
			case 0 { revert(data, len) }
			default { return(data, len) }
		}
	}
}
