import React, { useRef } from 'react';
import { Image, Typography, Space, Button } from 'antd';
import { useRouter } from 'next/router';
import { useIntl } from 'react-intl';

import InfoModal from '@/components/modal/infoModal';
import { useJoin, useFollow } from './useHooks';

import { useAppSelector, useAppDispatch } from '@/store/hooks';
import { setCurrentDAO, setDAOType } from '@/store/features/daoSlice';

import WalletModal from '@/components/modal/walletModal';

const { Paragraph } = Typography;

const DAOItem = (props: any) => {
  const { formatMessage } = useIntl();
  const dispatch = useAppDispatch();
  const router = useRouter();
  const { status1 = 1, status2 = 1, id, isJoin, isLike, isMember } = props.data;

  const { web3, address, chainId } = useAppSelector((store) => store.wallet);
  const { nickname } = useAppSelector((store) => store.user.userInfo);
  const { isInit } = useAppSelector((store) => store.common);

  const { join, setJoin, loading } = useJoin(
    props.data.root,
    props.data.member,
    address,
    isJoin,
    isMember,
  );
  const { follow, setFollow } = useFollow(id, chainId, isLike);

  // const [loading, setLoading] = useState(false);

  const infoModal: any = useRef(null);
  const walletModal: any = useRef(null);

  const img =
    'data:image/png;base64,';

  const imgs = [
    'data:image/png;base64,',
    'data:image/png;base64,',
    'data:image/png;base64,',
    'data:image/png;base64,',
    'data:image/png;base64,',
    'data:image/png;base64,',
  ];

  const avatars = imgs.slice(0, Math.min(props.data.members || 1, 4));

  const handleClick = () => {
    // getMemberId(); // test
    if (!isInit) {
      walletModal.current.show();
      return;
    }

    if (!nickname) {
      infoModal.current.show();
      return;
    }

    const type = join ? 'join' : follow ? 'follow' : 'visitor';
    dispatch(setDAOType(type));
    dispatch(setCurrentDAO(props.data));
    router.push('/dashboard/mine/home');
  };

  const handleJoinClick = () => {
    if (!isInit) {
      walletModal.current.show();
      return;
    }

    if (!nickname) {
      infoModal.current.show();
      return;
    }

    // infoModal.current.show();
    // dispatch(setCurrentDAO(props.data)); // waitBlockNumber用到
    dispatch(setCurrentDAO(props.data)); // 要加入的 DAO 中创建提案

    setJoin();

    // setLoading(true);

    // try {
    //   await joinDAO({ contractAddress: props.data.member });
    //   setLoading(false);
    //   setJoin();
    // } catch (error) {
    //   console.error(error);
    //   setLoading(false);
    // }
  };

  const handleFollowClick = () => {
    if (!isInit) {
      walletModal.current.show();
      return;
    }

    if (!nickname) {
      infoModal.current.show();
      return;
    }

    setFollow();
  };

  return (
    <div className="item">
      <Image
        style={{ borderRadius: 10, cursor: 'pointer' }}
        src={props.data.image || img}
        width={196}
        height={196}
        preview={false}
        alt="avatar"
        onClick={handleClick}
      />
      <div>
        <Paragraph
          className="name"
          style={{ width: 196 }}
          ellipsis={{ rows: 2 }}
        >
          {props.data.name}
        </Paragraph>
      </div>
      {/* <div className="name">
        {props.data.name}
      </div> */}
      <div className="imgs">
        <Space size={6} align="center">
          {avatars.map((item, index) => (
            <Image
              style={{ borderRadius: '50%' }}
              src={props.data.avatar || item}
              width={20}
              height={20}
              preview={false}
              alt="avatar"
              key={index}
            />
          ))}
          <span className="total">
            {formatMessage(
              { id: 'home.total.member' },
              { value: props.data.members || 1 },
            )}
          </span>
        </Space>
      </div>
      <div className="buttons">
        <Button
          className="button-light"
          type="primary"
          disabled={join}
          onClick={handleJoinClick}
          loading={loading}
        >
          {formatMessage({ id: join ? 'home.joined' : 'home.join' })}
        </Button>
        <Button type="primary" shape="round" onClick={handleFollowClick}>
          {/* {follow ? 'Followed' : 'Follow'} */}
          {formatMessage({ id: follow ? 'home.followed' : 'home.follow' })}
        </Button>
      </div>

      <InfoModal ref={infoModal} />
      <WalletModal ref={walletModal} />

      <style jsx>
        {`
          .item {
            display: flex;
            flex-direction: column;
          }

          .name {
            margin-top: 16px;
            font-size: 16px;
            color: #232323;
            line-height: 24px;
          }

          .item :global(.name) {
            height: 48px;
            margin-top: 16px;
            font-size: 16px;
            color: #232323;
            line-height: 24px;
          }

          .imgs {
            margin-top: 10px;
          }

          .total {
            font-size: 12px;
            color: #969ba0;
            line-height: 24px;
          }

          .buttons {
            display: flex;
            justify-content: space-between;
            margin-top: 17px;
          }

          .buttons :global(button) {
            height: 40px;
            font-size: 16px;
            border: 0;
          }

          .buttons :global(.button-light) {
            font-size: 16px;
            color: #2f4cdd;
            background-color: #ebedfc;
            border: 0;
          }

          .buttons :global(.button-light:disabled) {
            color: rgba(0, 0, 0, 0.25);
            background-color: rgba(0, 0, 0, 0.04);
            box-shadow: none;
          }
        `}
      </style>
    </div>
  );
};

export default DAOItem;
